Date today = new Date();
long myTimeStamp = Long.parseLong(new SimpleDateFormat("yyMMddHHmmss").format(today));

// nom du fichier logs
globalMap.put("Log_File", jobName + "_" + myTimeStamp + ".csv");

globalMap.put("JobTimeStamp", today);
globalMap.put("JobDate", Integer.parseInt(Integer.toString(today.getYear()) + new SimpleDateFormat("DDD").format(today)));
globalMap.put("JobTime", Integer.parseInt(new SimpleDateFormat("HHmmss").format(today)));

// date d'effet des tarifs : 1er jour du mois en cours
globalMap.put("date_tarifs", JDE.convertToJulian(today));
// date d'effet des taux de change : 1er jour du mois en cours
globalMap.put("date_devise", JDE.convertToJulian(TalendDate.getFirstDayOfMonth(today)));

System.out.println("Date tarifs = " + (int)globalMap.get("date_tarifs"));
System.out.println("Date devise = " + (int)globalMap.get("date_devise"));
